import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const ZakatAndDonationsRecepients = () => {
  const [users, setUsers] = useState(null);

  useEffect(() => {
    console.log('here');
    fetch("http://localhost:8000/users/all")
      .then(response => response.json())
      .then(data => {
        console.log('here');
        setUsers(data);
      })
      .catch(err => console.log('Error fetching users:', err));
  }, []);

  useEffect(() => {
    console.log(users);
  }, [users]);

  return (
    <div class="container profile-page">
      <div class="row">
        <div class="col-xl-6 col-lg-7 col-md-12">
          <div class="card profile-header">
            <div class="body">
              <div class="row">
                <div class="col-lg-4 col-md-4 col-12">
                  <div class="profile-image float-md-right">
                    {" "}
                    <img
                      src="https://bootdey.com/img/Content/avatar/avatar7.png"
                      alt=""
                    />{" "}
                  </div>
                </div>
                <div class="col-lg-8 col-md-8 col-12">
                  <h4 class="m-t-0 m-b-0">
                    <strong>Michael</strong> Deo
                  </h4>
                  <span class="job_post">Deserving</span>
                  <p>
                    795 Folsom Ave, Suite 600 San Francisco, CADGE 94107 <br />
                    Verified <i className="fas fa-check"></i>
                  </p>
                  <Link to="/recepient">
                    {/* <button class="btn btn-primary btn-round">Follow</button> */}
                    <button class="btn btn-primary btn-round btn-simple">
                      Donate
                    </button>
                  </Link>
                  <p class="social-icon m-t-5 m-b-0">
                    <a title="Twitter" href="/">
                      <i class="fa fa-twitter"></i>
                    </a>
                    <a title="Facebook" href="/">
                      <i class="fa fa-facebook"></i>
                    </a>
                    <a title="Google-plus" href="/">
                      <i class="fa fa-twitter"></i>
                    </a>
                    <a title="Behance" href="/">
                      <i class="fa fa-behance"></i>
                    </a>
                    <a title="Instagram" href="/">
                      <i class="fa fa-instagram "></i>
                    </a>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ZakatAndDonationsRecepients;
