import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Recipient = () => {
  const [user, setUser] = useState(null);
  const { id } = useParams();

  console.log(id);

  useEffect(() => {
    fetch(`http://localhost:8000/user/get/${id}`)
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(err => console.log('Error fetching users:', err));
  }, [id]);

  useEffect(() => {
    console.log(user);
  }, [user]);

  return (
    <div className="container bootstrap snippets bootdey">
      <div className="row">
        {user && (
          <div className="profile-nav col-md-3">
            <div className="panel">
              <div className="user-heading round">
                <a href="/">
                  <img
                    src={`http://localhost:8000/${user.picture}`}
                    alt=""
                  />
                </a>
                <h1>{user.name}</h1>
              </div>

              {/* <ul className="nav nav-pills nav-stacked">
                <li className="active">
                  <a href="/">
                    <i className="fa fa-user"></i> Profile
                  </a>
                </li>
                <li>
                  <a href="/">
                    <i className="fa fa-calendar"></i> Recent Activity
                    <span className="label label-warning pull-right r-activity">
                      9
                    </span>
                  </a>
                </li>
                <li>
                  <a href="/">
                    <i className="fa fa-edit"></i> Edit profile
                  </a>
                </li>
              </ul> */}
            </div>
          </div>
        )}

        <div className="profile-info col-md-9">
          <div className="panel">
            <div className="bio-graph-heading">
              {user?.description}
            </div>
            <div className="panel-body bio-graph-info">
              <h1>Bio Graph</h1>
              <div className="row">
                <div className="bio-row">
                  <p>
                    <span>Name </span>: {user?.name}
                  </p>
                </div>
                <div className="bio-row">
                  <p>
                    <span>Country </span>: Pakistan
                  </p>
                </div>
                <div className="bio-row">
                  <p>
                    <span>City</span>: {user?.city?.name}
                  </p>
                </div>
                {/* <div className="bio-row">
                  <p>
                    <span>Occupation </span>: UI Designer
                  </p>
                </div> */}
                <div className="bio-row">
                  <p>
                    <span>Phone </span>: {user?.phone_number}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Recipient;
